mapper.insert:
INSERT INTO t_review
set
iuser = #{iuser}
,ipayment = #{ipayment}
, contents = #{contents}
, rating = #{rating}

mapper.select:
SELECT COUNT(ireview)
FROM t_review
WHERE ipayment = #{ipayment}

mapper.update:








update t_payment_status
set istatus = 1
where ipayment = #{ipayment}

update t_review
set

    contents = #{contents}


    ,rating = #{rating}



where ireview = #{ireview}

mapper.select:
SELECT istatus
FROM t_payment_status
WHERE ipayment = #{ipayment} and iuser = #{iuser}

select count(PA.ipayment) as isExists, if(PA.ibuyer = #{iuser}, 1, 0) as isBuyer
from t_product_payment PP
join t_product P on PP.iproduct = P.iproduct
join t_payment PA on PP.ipayment = PA.ipayment and PA.ipayment = #{ipayment}
where P.iuser = #{iuser} or PA.ibuyer = #{iuser}

select iuser, ipayment
FROM t_review
WHERE ireview = #{ireview}

mapper.update:
update t_user
set rating = #{rating}
where iuser = #{iuser}

mapper.select:
SELECT rating
FROM t_review
WHERE ireview = #{ireview}
