mapper.insert:
INSERT INTO t_user
SET addr = #{addr}

, iauth = #{iauth}

, rest_addr = #{restAddr}
, uid = #{uid}
, upw = #{upw}
, nick = #{nick}
, phone = #{phone}
, email = #{email}
, y = #{y}
, x = #{x}

INSERT INTO t_comp_info
set comp_code = #{compCode}
, comp_nm = #{compNm}
, iuser = #{iuser}

mapper.update:
UPDATE t_user
SET firebase_token = #{firebaseToken}
WHERE iuser = #{iuser}

mapper.select:
SELECT uid, iuser, iauth
FROM t_user
WHERE phone = #{phone}

